mapper.insert:
	    
	insert into sys_attr (
						      F_ATTR_TABLE,  
				          F_ATTR_NAME
	) values (
						       #{F_ATTR_TABLE},  
				             #{F_ATTR_NAME}
	)
	
	
          SELECT LAST_INSERT_ID() AS id

mapper.delete:
delete from  sys_attr
where 
	F_ATTR_UID = #{ F_ATTR_UID}
