mapper.insert:
	    
	insert into tb_product_attr (
						      F_PRODUCT_UID,  
				          F_ATTR_UID
	) values (
						       #{F_PRODUCT_UID},  
				             #{F_ATTR_UID}
	)
	
	
          SELECT LAST_INSERT_ID() AS id

mapper.delete:
delete from  tb_product_attr
where 
	F_PRODUCT_ATTR_UID = #{ F_PRODUCT_ATTR_UID}
