mapper.insert:
	    
	insert into tb_shouchang (
						      F_USER_ID,  
						      F_PRODUCT_ID,  
				          CREATE_DATE
	) values (
						       #{F_USER_ID},  
						       #{F_PRODUCT_ID},  
				             #{CREATE_DATE}
	)
	
	
          SELECT LAST_INSERT_ID() AS id

mapper.delete:
delete from  tb_shouchang
where 
	F_SHOUCHANG_UID = #{ F_SHOUCHANG_UID}
